"use client"

import * as React from "react"
import { GripVerticalIcon } from "lucide-react"
import * as ResizablePrimitive from "react-resizable-panels"

import { cn } from "@/lib/utils"

function ResizablePanelGroup({
  className,
  ...props
}: React.ComponentProps<typeof ResizablePrimitive.PanelGroup>) {
  return (
    <ResizablePrimitive.PanelGroup
      data-slot="resizable-panel-group"
      className={cn(
        "flex h-full w-full data-[panel-group-direction=vertical]:flex-col",
        className
      )}
      {...props}
    />
  )
}

function ResizablePanel({
  ...props
}: React.ComponentProps<typeof ResizablePrimitive.Panel>) {
  return <ResizablePrimitive.Panel data-slot="resizable-panel" {...props} />
}

function ResizableHandle({
  withHandle,
  className,
  ...props
}: React.ComponentProps<typeof ResizablePrimitive.PanelResizeHandle> & {
  withHandle?: boolean
}) {
  return (
    <ResizablePrimitive.PanelResizeHandle
      data-slot="resizable-handle"
      className={cn(
        "bg-border focus-visible:ring-ring relative flex w-px items-center justify-center after:absolute after:inset-y-0 after:left-1/2 after:w-1 after:-translate-x-1/2 focus-visible:ring-1 focus-visible:ring-offset-1 focus-visible:outline-hidden data-[panel-group-direction=vertical]:h-px data-[panel-group-direction=vertical]:w-full data-[panel-group-direction=vertical]:after:left-0 data-[panel-group-direction=vertical]:after:h-1 data-[panel-group-direction=vertical]:after:w-full data-[panel-group-direction=vertical]:after:translate-x-0 data-[panel-group-direction=vertical]:after:-translate-y-1/2 [&[data-panel-group-direction=vertical]>div]:rotate-90",
        className
      )}
      {...props}
    >
      {withHandle && (
        <div className="bg-border z-10 flex h-4 w-3 items-center justify-center rounded-xs border">
          <GripVerticalIcon className="size-2.5" />
        </div>
      )}
    </ResizablePrimitive.PanelResizeHandle>
  )
}

export { ResizablePanelGroup, ResizablePanel, ResizableHandle }

src/i18n/request.ts.content:
import { getRequestConfig } from 'next-intl/server';
import { cookies } from 'next/headers';

export default getRequestConfig(async () => {
  const cookieStore = await cookies();
  const localeCookie = cookieStore.get('NEXT_LOCALE');
  const locale = localeCookie?.value || 'id';

  return {
    locale,
    messages: (await import(`../../messages/${locale}.json`)).default
  };
});

src/app/admin/dashboard/packages/page.ts.content:
"use client";

import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Plus, Edit, Trash2, X } from "lucide-react";
import Image from "next/image";
import { PackageWithCity, City } from "@/types";

export default function PackagesManagementPage() {
  const [packages, setPackages] = useState<PackageWithCity[]>([]);
  const [cities, setCities] = useState<City[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingPackage, setEditingPackage] = useState<PackageWithCity | null>(null);
  const [formData, setFormData] = useState({
    cityId: "",
    name: "",
    description: "",
    price: "",
    durationDays: "",
    imageUrl: "",
    features: "",
  });
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    loadPackages();
    loadCities();
  }, []);

  const loadPackages = async () => {
    try {
      const response = await fetch('/api/packages');
      if (!response.ok) throw new Error('Failed to load packages');
      const data = await response.json();
      setPackages(data || []);
    } catch (error) {
      console.error("Error loading packages:", error);
    } finally {
      setLoading(false);
    }
  };

  const loadCities = async () => {
    try {
      const response = await fetch('/api/cities');
      if (!response.ok) throw new Error('Failed to load cities');
      const data = await response.json();
      setCities(data || []);
    } catch (error) {
      console.error("Error loading cities:", error);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const featuresArray = formData.features.split('\n').filter(f => f.trim());

    try {
      if (editingPackage) {
        if (file) {
          const fd = new FormData();
          fd.append('cityId', formData.cityId);
          fd.append('name', formData.name);
          fd.append('description', formData.description);
          fd.append('price', String(parseFloat(formData.price)));
          fd.append('durationDays', String(parseInt(formData.durationDays)));
          fd.append('features', featuresArray.join('\n'));
          fd.append('image', file);
          const response = await fetch(`/api/packages/${editingPackage.id}`, { method: 'PUT', body: fd });
          if (!response.ok) throw new Error('Failed to update package');
        } else {
          const payload = {
            cityId: formData.cityId,
            name: formData.name,
            description: formData.description,
            price: parseFloat(formData.price),
            durationDays: parseInt(formData.durationDays),
            imageUrl: formData.imageUrl,
            features: featuresArray,
          };
          const response = await fetch(`/api/packages/${editingPackage.id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
          });
          if (!response.ok) throw new Error('Failed to update package');
        }
      } else {
        if (file) {
          const fd = new FormData();
          fd.append('cityId', formData.cityId);
          fd.append('name', formData.name);
          fd.append('description', formData.description);
          fd.append('price', String(parseFloat(formData.price)));
          fd.append('durationDays', String(parseInt(formData.durationDays)));
          fd.append('features', featuresArray.join('\n'));
          fd.append('image', file);
          const response = await fetch('/api/packages', { method: 'POST', body: fd });
          if (!response.ok) throw new Error('Failed to create package');
        } else {
          const payload = {
            cityId: formData.cityId,
            name: formData.name,
            description: formData.description,
            price: parseFloat(formData.price),
            durationDays: parseInt(formData.durationDays),
            imageUrl: formData.imageUrl,
            features: featuresArray,
          };
          const response = await fetch('/api/packages', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
          });
          if (!response.ok) throw new Error('Failed to create package');
        }
      }

      resetForm();
      loadPackages();
    } catch (error) {
      console.error("Error saving package:", error);
      alert("Failed to save package");
    }
  };

  const handleEdit = (pkg: PackageWithCity) => {
    setEditingPackage(pkg);
    setFormData({
      cityId: pkg.cityId,
      name: pkg.name,
      description: pkg.description,
      price: pkg.price.toString(),
      durationDays: pkg.durationDays.toString(),
      imageUrl: pkg.imageUrl,
      features: Array.isArray(pkg.features) ? pkg.features.join('\n') : '',
    });
    setFile(null);
    setShowForm(true);
  };

  const handleDelete = async (id: string) => {
    if (!confirm("Are you sure you want to delete this package?")) return;

    try {
      const response = await fetch(`/api/packages/${id}`, { method: 'DELETE' });
      if (!response.ok) throw new Error('Failed to delete package');
      loadPackages();
    } catch (error) {
      console.error("Error deleting package:", error);
      alert("Failed to delete package");
    }
  };

  const resetForm = () => {
    setFormData({
      cityId: "",
      name: "",
      description: "",
      price: "",
      durationDays: "",
      imageUrl: "",
      features: "",
    });
    setFile(null);
    setEditingPackage(null);
    setShowForm(false);
  };

  if (loading) {
    return <div className="flex items-center justify-center p-12">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Packages Management</h1>
          <p className="text-muted-foreground">Manage travel packages</p>
        </div>
        <Button onClick={() => setShowForm(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Add Package
        </Button>
      </div>

      {showForm && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>{editingPackage ? "Edit Package" : "Add New Package"}</CardTitle>
              <Button variant="ghost" size="icon" onClick={resetForm}>
                <X className="h-4 w-4" />
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid gap-4 md:grid-cols-2">
                <div>
                  <label className="text-sm font-medium">City</label>
                  <Select value={formData.cityId} onValueChange={(value) => setFormData({ ...formData, cityId: value })}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select a city" />
                    </SelectTrigger>
                    <SelectContent>
                      {cities.map((city) => (
                        <SelectItem key={city.id} value={city.id}>
                          {city.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div>
                  <label className="text-sm font-medium">Package Name</label>
                  <Input
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <label className="text-sm font-medium">Price (USD)</label>
                  <Input
                    type="number"
                    step="0.01"
                    value={formData.price}
                    onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <label className="text-sm font-medium">Duration (Days)</label>
                  <Input
                    type="number"
                    value={formData.durationDays}
                    onChange={(e) => setFormData({ ...formData, durationDays: e.target.value })}
                    required
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="text-sm font-medium">Image URL (optional)</label>
                  <Input
                    value={formData.imageUrl}
                    onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                    placeholder="https://example.com/image.jpg"
                  />
                  <div className="mt-3">
                    <label className="text-sm font-medium">Or Upload Image</label>
                    <Input type="file" accept="image/*" onChange={(e) => setFile(e.target.files?.[0] || null)} />
                    {file && (
                      <p className="mt-1 text-xs text-gray-500">Selected: {file.name}</p>
                    )}
                  </div>
                </div>
                <div className="md:col-span-2">
                  <label className="text-sm font-medium">Description</label>
                  <Textarea
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    rows={3}
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="text-sm font-medium">Features (one per line)</label>
                  <Textarea
                    value={formData.features}
                    onChange={(e) => setFormData({ ...formData, features: e.target.value })}
                    rows={4}
                    placeholder="Free WiFi&#10;Breakfast included&#10;Airport pickup"
                  />
                </div>
              </div>
              <div className="flex gap-2">
                <Button type="submit">
                  {editingPackage ? "Update" : "Create"} Package
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {packages.map((pkg) => (
          <Card key={pkg.id}>
            <CardContent className="p-0">
              {pkg.imageUrl && (
                <div className="relative h-48 w-full">
                  <Image
                    src={pkg.imageUrl}
                    alt={pkg.name}
                    fill
                    className="rounded-t-lg object-cover"
                  />
                </div>
              )}
              <div className="p-4 space-y-2">
                <h3 className="font-bold text-lg">{pkg.name}</h3>
                <p className="text-sm text-muted-foreground">{pkg.city.name}</p>
                <p className="text-sm line-clamp-2">{pkg.description}</p>
                <div className="flex items-center justify-between pt-2">
                  <div>
                    <p className="text-2xl font-bold">${pkg.price}</p>
                    <p className="text-xs text-muted-foreground">{pkg.durationDays} days</p>
                  </div>
                  <div className="flex gap-2">
                    <Button size="sm" variant="outline" onClick={() => handleEdit(pkg)}>
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button size="sm" variant="destructive" onClick={() => handleDelete(pkg.id)}>
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {packages.length === 0 && !showForm && (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-gray-600">No packages found. Add your first package to get started.</p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

