import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import path from 'path';
import { promises as fs } from 'fs';

export const runtime = 'nodejs';

export async function GET(
  request: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    const pkg = await prisma.package.findUnique({
      where: { id: params.id },
      include: {
        city: true,
      },
    });

    if (!pkg) {
      return NextResponse.json(
        { error: 'Package not found' },
        { status: 404 }
      );
    }

    return NextResponse.json(pkg);
  } catch (error) {
    console.error('Error fetching package:', error);
    return NextResponse.json(
      { error: 'Failed to fetch package' },
      { status: 500 }
    );
  }
}

export async function PUT(
  request: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    const contentType = request.headers.get('content-type') || '';
    let cityId = '';
    let name = '';
    let description = '';
    let price: number | undefined = undefined;
    let durationDays: number | undefined = undefined;
    let imageUrl: string | undefined = undefined;
    let features: string[] | undefined = undefined;

    if (contentType.includes('multipart/form-data')) {
      const form = await request.formData();
      cityId = String(form.get('cityId') || '');
      name = String(form.get('name') || '');
      description = String(form.get('description') || '');
      const priceRaw = form.get('price');
      if (priceRaw !== null) price = parseFloat(String(priceRaw));
      const durationRaw = form.get('durationDays');
      if (durationRaw !== null) durationDays = parseInt(String(durationRaw));
      const featuresRaw = String(form.get('features') || '')
        .split('\n')
        .map(s => s.trim())
        .filter(Boolean);
      if (featuresRaw.length) features = featuresRaw;
      const file = form.get('image') as File | null;
      if (file && file.size > 0) {
        const arrayBuffer = await file.arrayBuffer();
        const buffer = Buffer.from(arrayBuffer);
        const uploadsDir = path.join(process.cwd(), 'public', 'uploads');
        await fs.mkdir(uploadsDir, { recursive: true });
        const safeName = file.name.replace(/[^a-zA-Z0-9_.-]/g, '_');
        const filename = `${Date.now()}_${safeName}`;
        const outPath = path.join(uploadsDir, filename);
        await fs.writeFile(outPath, buffer);
        imageUrl = `/uploads/${filename}`;
      } else {
        const fromField = String(form.get('imageUrl') || '');
        if (fromField) imageUrl = fromField;
      }
    } else {
      const body = await request.json();
      cityId = body.cityId;
      name = body.name;
      description = body.description;
      price = body.price;
      durationDays = body.durationDays;
      imageUrl = body.imageUrl;
      features = body.features;
    }

    const data: any = { name, description };
    if (cityId) data.cityId = cityId;
    if (price !== undefined) data.price = price;
    if (durationDays !== undefined) data.durationDays = durationDays;
    if (imageUrl !== undefined) data.imageUrl = imageUrl;
    if (features !== undefined) data.features = features;

    const updatedPackage = await prisma.package.update({
      where: { id: params.id },
      data,
      include: { city: true },
    });

    return NextResponse.json(updatedPackage);
  } catch (error) {
    console.error('Error updating package:', error);
    return NextResponse.json(
      { error: 'Failed to update package' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    await prisma.package.delete({
      where: { id: params.id },
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error deleting package:', error);
    return NextResponse.json(
      { error: 'Failed to delete package' },
      { status: 500 }
    );
  }
}

