src/app/admin/dashboard/hero-banners/pag.content:
"use client";

import { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Plus, Edit, Trash2, X } from "lucide-react";
import Image from "next/image";
import { HeroBanner } from "@/types";

export default function HeroBannersPage() {
  const [banners, setBanners] = useState<HeroBanner[]>([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingBanner, setEditingBanner] = useState<HeroBanner | null>(null);
  const [formData, setFormData] = useState({
    title: "",
    subtitle: "",
    imageUrl: "",
    isActive: true,
    order: "",
  });
  const [file, setFile] = useState<File | null>(null);

  useEffect(() => {
    loadBanners();
  }, []);

  const loadBanners = async () => {
    try {
      const response = await fetch('/api/hero-banners');
      if (!response.ok) throw new Error('Failed to load hero banners');
      const data = await response.json();
      setBanners(data || []);
    } catch (error) {
      console.error("Error loading hero banners:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      if (editingBanner) {
        if (file) {
          const fd = new FormData();
          fd.append('title', formData.title);
          fd.append('subtitle', formData.subtitle);
          fd.append('isActive', String(formData.isActive));
          fd.append('order', String(parseInt(formData.order) || 0));
          fd.append('image', file);
          const response = await fetch(`/api/hero-banners/${editingBanner.id}`, { method: 'PUT', body: fd });
          if (!response.ok) throw new Error('Failed to update hero banner');
        } else {
          const payload = {
            title: formData.title,
            subtitle: formData.subtitle,
            imageUrl: formData.imageUrl,
            isActive: formData.isActive,
            order: parseInt(formData.order) || 0,
          };
          const response = await fetch(`/api/hero-banners/${editingBanner.id}`, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
          });
          if (!response.ok) throw new Error('Failed to update hero banner');
        }
      } else {
        if (file) {
          const fd = new FormData();
          fd.append('title', formData.title);
          fd.append('subtitle', formData.subtitle);
          fd.append('isActive', String(formData.isActive));
          fd.append('order', String(parseInt(formData.order) || 0));
          fd.append('image', file);
          const response = await fetch('/api/hero-banners', { method: 'POST', body: fd });
          if (!response.ok) throw new Error('Failed to create hero banner');
        } else {
          const payload = {
            title: formData.title,
            subtitle: formData.subtitle,
            imageUrl: formData.imageUrl,
            isActive: formData.isActive,
            order: parseInt(formData.order) || 0,
          };
          const response = await fetch('/api/hero-banners', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload),
          });
          if (!response.ok) throw new Error('Failed to create hero banner');
        }
      }

      resetForm();
      loadBanners();
    } catch (error) {
      console.error("Error saving hero banner:", error);
      alert("Failed to save hero banner");
    }
  };

  const handleEdit = (banner: HeroBanner) => {
    setEditingBanner(banner);
    setFormData({
      title: banner.title,
      subtitle: banner.subtitle,
      imageUrl: banner.imageUrl,
      isActive: banner.isActive,
      order: banner.order.toString(),
    });
    setFile(null);
    setShowForm(true);
  };

  const handleDelete = async (id: string) => {
    if (!confirm("Are you sure you want to delete this hero banner?")) return;

    try {
      const response = await fetch(`/api/hero-banners/${id}`, { method: 'DELETE' });
      if (!response.ok) throw new Error('Failed to delete hero banner');
      loadBanners();
    } catch (error) {
      console.error("Error deleting hero banner:", error);
      alert("Failed to delete hero banner");
    }
  };

  const resetForm = () => {
    setFormData({
      title: "",
      subtitle: "",
      imageUrl: "",
      isActive: true,
      order: "",
    });
    setFile(null);
    setEditingBanner(null);
    setShowForm(false);
  };

  if (loading) {
    return <div className="flex items-center justify-center p-12">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Hero Banners Management</h1>
          <p className="text-muted-foreground">Manage hero banners displayed on homepage</p>
        </div>
        <Button onClick={() => setShowForm(true)}>
          <Plus className="mr-2 h-4 w-4" />
          Add Hero Banner
        </Button>
      </div>

      {showForm && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>{editingBanner ? "Edit Hero Banner" : "Add New Hero Banner"}</CardTitle>
              <Button variant="ghost" size="icon" onClick={resetForm}>
                <X className="h-4 w-4" />
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="grid gap-4 md:grid-cols-2">
                <div>
                  <label className="text-sm font-medium">Title</label>
                  <Input
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                    required
                  />
                </div>
                <div>
                  <label className="text-sm font-medium">Order</label>
                  <Input
                    type="number"
                    value={formData.order}
                    onChange={(e) => setFormData({ ...formData, order: e.target.value })}
                    placeholder="0"
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="text-sm font-medium">Subtitle</label>
                  <Textarea
                    value={formData.subtitle}
                    onChange={(e) => setFormData({ ...formData, subtitle: e.target.value })}
                    rows={2}
                  />
                </div>
                <div className="md:col-span-2">
                  <label className="text-sm font-medium">Image URL (optional)</label>
                  <Input
                    value={formData.imageUrl}
                    onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                    placeholder="https://example.com/image.jpg"
                  />
                  <div className="mt-3">
                    <label className="text-sm font-medium">Or Upload Image</label>
                    <Input type="file" accept="image/*" onChange={(e) => setFile(e.target.files?.[0] || null)} />
                    {file && (
                      <p className="mt-1 text-xs text-gray-500">Selected: {file.name}</p>
                    )}
                  </div>
                </div>
                <div className="flex items-center space-x-2">
                  <Switch
                    checked={formData.isActive}
                    onCheckedChange={(checked) => setFormData({ ...formData, isActive: checked })}
                  />
                  <label className="text-sm font-medium">Active</label>
                </div>
              </div>
              <div className="flex gap-2">
                <Button type="submit">
                  {editingBanner ? "Update" : "Create"} Hero Banner
                </Button>
                <Button type="button" variant="outline" onClick={resetForm}>
                  Cancel
                </Button>
              </div>
            </form>
          </CardContent>
        </Card>
      )}

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {banners.map((banner) => (
          <Card key={banner.id}>
            <CardContent className="p-0">
              {banner.imageUrl && (
                <div className="relative h-48 w-full">
                  <Image
                    src={banner.imageUrl}
                    alt={banner.title}
                    fill
                    className="rounded-t-lg object-cover"
                  />
                </div>
              )}
              <div className="p-4 space-y-2">
                <div className="flex items-start justify-between">
                  <h3 className="font-bold text-lg">{banner.title}</h3>
                  <span className={`text-xs px-2 py-1 rounded ${banner.isActive ? 'bg-green-100 text-green-700' : 'bg-gray-100 text-gray-700'}`}>
                    {banner.isActive ? 'Active' : 'Inactive'}
                  </span>
                </div>
                <p className="text-sm text-muted-foreground line-clamp-2">{banner.subtitle}</p>
                <div className="flex items-center justify-between pt-2">
                  <p className="text-sm text-muted-foreground">Order: {banner.order}</p>
                  <div className="flex gap-2">
                    <Button size="sm" variant="outline" onClick={() => handleEdit(banner)}>
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button size="sm" variant="destructive" onClick={() => handleDelete(banner.id)}>
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {banners.length === 0 && !showForm && (
        <Card>
          <CardContent className="py-12 text-center">
            <p className="text-gray-600">No hero banners found. Add your first hero banner to get started.</p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

