@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');

@import "tailwindcss";
@import "tailwindcss/utilities" layer(utilities);
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme {
  /* Shadcn/UI colors */
  --color-background: #ffffff;
  --color-foreground: #112211; /* Dark Charcoal */

  --color-card: #ffffff;
  --color-card-foreground: #112211;

  --color-popover: #ffffff;
  --color-popover-foreground: #112211;

  --color-primary: #dcac56; /* Primary Gold */
  --color-primary-foreground: #112211; /* Dark Charcoal */

  --color-secondary: #e6c07c; /* Gold Light */
  --color-secondary-foreground: #112211; /* Dark Charcoal */

  --color-muted: #E2E8F0; /* Light Gray */
  --color-muted-foreground: #4A5568; /* Medium Gray */

  --color-accent: #e6c07c; /* Gold Light */
  --color-accent-foreground: #112211; /* Dark Charcoal */

  --color-destructive: #ef4444;
  --color-destructive-foreground: #fafafa;

  --color-border: #E2E8F0; /* Light Gray */
  --color-input: #E2E8F0; /* Light Gray */
  --color-ring: #dcac56; /* Primary Gold */

  --color-chart-1: #dcac56; /* Primary Gold */
  --color-chart-2: #F7E9B5; /* Warning Yellow */
  --color-chart-3: #D6E4F7; /* Info Blue */
  --color-chart-4: #FFB800; /* Star Rating */
  --color-chart-5: #dcac56; /* Primary Gold */

  --color-sidebar: #f7f7f7;
  --color-sidebar-foreground: #404040;
  --color-sidebar-primary: #1a1a1a;
  --color-sidebar-primary-foreground: #fafafa;
  --color-sidebar-accent: #f0f0f0;
  --color-sidebar-accent-foreground: #1a1a1a;
  --color-sidebar-border: #d4d4d8;
  --color-sidebar-ring: #3b82f6;

  /* Additional design system colors */
  --color-gold-light: #e6c07c;
  --color-gold-pale: #f0d699;
  --color-dark-charcoal: #112211;
  --color-medium-gray: #4A5568;
  --color-light-gray: #E2E8F0;
  --color-background-white: #FAFAFA;
  --color-success-gold: #dcac56;
  --color-warning-yellow: #F7E9B5;
  --color-info-blue: #D6E4F7;
  --color-star-rating: #FFB800;
  --color-black-overlay: #000000; /* For rgba usage */

  /* Typography */
  --font-sans: "Inter", sans-serif;
  --font-display: "Inter", sans-serif;
  --font-body: "Inter", sans-serif;

  --radius-lg: 0.75rem; /* Adjusted for cards and larger elements */
  --radius-md: 0.5rem; /* Adjusted for buttons and inputs */
  --radius-sm: 0.25rem; /* Adjusted for small elements */

  --animate-accordion-down: accordion-down 0.2s ease-out;
  --animate-accordion-up: accordion-up 0.2s ease-out;

  @keyframes accordion-down {
    from {
      height: 0;
    }
    to {
      height: var(--radix-accordion-content-height);
    }
  }
  @keyframes accordion-up {
    from {
      height: var(--radix-accordion-content-height);
    }
    to {
      height: 0;
    }
  }
}

/*
  The default border color has changed to `currentColor` in Tailwind CSS v4,
  so we've added these compatibility styles to make sure everything still
  looks the same as it did with Tailwind CSS v3.

  If we ever want to remove these styles, we need to add an explicit border
  color utility to any element that depends on these defaults.
*/
@layer base {
  *,
  ::after,
  ::before,
  ::backdrop,
  ::file-selector-button {
    border-color: var(--color-border);
  }
}

@layer utilities {
  body {
    font-family: var(--font-body);
  }
}

@layer base {
  :root {
    --background: var(--color-background);
    --foreground: var(--color-foreground);
    --card: var(--color-card);
    --card-foreground: var(--color-card-foreground);
    --popover: var(--color-popover);
    --popover-foreground: var(--color-popover-foreground);
    --primary: var(--color-primary);
    --primary-foreground: var(--color-primary-foreground);
    --secondary: var(--color-secondary);
    --secondary-foreground: var(--color-secondary-foreground);
    --muted: var(--color-muted);
    --muted-foreground: var(--color-muted-foreground);
    --accent: var(--color-accent);
    --accent-foreground: var(--color-accent-foreground);
    --destructive: var(--color-destructive);
    --destructive-foreground: var(--color-destructive-foreground);
    --border: var(--color-border);
