import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';

export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;
    const origin = searchParams.get('origin');
    const destination = searchParams.get('destination');

    const where: any = {
      isActive: true,
    };

    if (origin) {
      where.origin = {
        contains: origin,
        mode: 'insensitive',
      };
    }

    if (destination) {
      where.destination = {
        contains: destination,
        mode: 'insensitive',
      };
    }

    const flights = await prisma.flightPackage.findMany({
      where,
      orderBy: {
        price: 'asc',
      },
    });

    return NextResponse.json(flights);
  } catch (error) {
    console.error('Error fetching flights:', error);
    return NextResponse.json(
      { error: 'Failed to fetch flights' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();

    const newFlight = await prisma.flightPackage.create({
      data: {
        airline: body.airline,
        flightNumber: body.flightNumber,
        origin: body.origin,
        destination: body.destination,
        departureTime: body.departureTime,
        arrivalTime: body.arrivalTime,
        price: body.price,
        class: body.class || 'economy',
        availableSeats: body.availableSeats || 0,
        duration: body.duration || '0h 0m',
        baggage: body.baggage || '20kg',
        isActive: body.isActive !== undefined ? body.isActive : true,
      },
    });

    return NextResponse.json(newFlight);
  } catch (error) {
    console.error('Error creating flight:', error);
    return NextResponse.json(
      { error: 'Failed to create flight' },
      { status: 500 }
    );
  }
}

src/app/api/cities/[id]/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import path from 'path';
import { promises as fs } from 'fs';

export const runtime = 'nodejs';

export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    const contentType = request.headers.get('content-type') || '';
    let name = '';
    let description = '';
    let imageUrl: string | undefined = undefined;

    if (contentType.includes('multipart/form-data')) {
      const form = await request.formData();
      name = String(form.get('name') || '');
      description = String(form.get('description') || '');
      const file = form.get('image') as File | null;
      if (file && file.size > 0) {
        const arrayBuffer = await file.arrayBuffer();
        const buffer = Buffer.from(arrayBuffer);
        const uploadsDir = process.env.UPLOADS_DIR || path.join(process.cwd(), 'uploads');
        await fs.mkdir(uploadsDir, { recursive: true });
        const safeName = file.name.replace(/[^a-zA-Z0-9_.-]/g, '_');
        const filename = `${Date.now()}_${safeName}`;
        const outPath = path.join(uploadsDir, filename);
        await fs.writeFile(outPath, buffer);
        imageUrl = `/api/images/${filename}`;
      } else {
        const fromField = String(form.get('imageUrl') || '');
        if (fromField) imageUrl = fromField;
      }
    } else {
      const body = await request.json();
      name = body.name;
      description = body.description || '';
      imageUrl = body.imageUrl;
    }

    const updatedCity = await prisma.city.update({
      where: { id },
      data: {
        name,
        description,
        ...(imageUrl !== undefined ? { imageUrl } : {}),
      },
    });

    return NextResponse.json(updatedCity);
  } catch (error) {
    console.error('Error updating city:', error);
    return NextResponse.json(
      { error: 'Failed to update city' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    await prisma.city.delete({
      where: { id },
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error deleting city:', error);
    return NextResponse.json(
      { error: 'Failed to delete city' },
      { status: 500 }
    );
  }
}

