                <div className="h-auto min-h-[80px] sm:min-h-[100px] max-w-full grow rounded-none border-0 p-3 sm:p-4 max-md:mx-1 max-md:border-b-2 md:my-1 md:w-1/2 md:border-r-2 border-[#E2E8F0]">
                  <DateSelector
                    label="Depart *"
                    sublabel="Saturday"
                    value={departDate}
                    onChange={setDepartDate}
                    minimal
