import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import path from 'path';
import { promises as fs } from 'fs';

export const runtime = 'nodejs';

export async function GET() {
  try {
    const heroBanners = await prisma.heroBanner.findMany({
      orderBy: { order: 'asc' },
    });

    return NextResponse.json(heroBanners);
  } catch (error) {
    console.error('Error fetching hero banners:', error);
    return NextResponse.json(
      { error: 'Failed to fetch hero banners' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const contentType = request.headers.get('content-type') || '';
    let title = '';
    let subtitle = '';
    let imageUrl = '';
    let isActive = true;
    let order = 0;

    if (contentType.includes('multipart/form-data')) {
      const form = await request.formData();
      title = String(form.get('title') || '');
      subtitle = String(form.get('subtitle') || '');
      isActive = String(form.get('isActive') || 'true') === 'true';
      order = parseInt(String(form.get('order') || '0')) || 0;
      const file = form.get('image') as File | null;
      if (file && file.size > 0) {
        const arrayBuffer = await file.arrayBuffer();
        const buffer = Buffer.from(arrayBuffer);
        const uploadsDir = path.join(process.cwd(), 'public', 'uploads');
        await fs.mkdir(uploadsDir, { recursive: true });
        const safeName = file.name.replace(/[^a-zA-Z0-9_.-]/g, '_');
        const filename = `${Date.now()}_${safeName}`;
        const outPath = path.join(uploadsDir, filename);
        await fs.writeFile(outPath, buffer);
        imageUrl = `/uploads/${filename}`;
      } else {
        imageUrl = String(form.get('imageUrl') || '');
      }
    } else {
      const body = await request.json();
      title = body.title;
      subtitle = body.subtitle || '';
      imageUrl = body.imageUrl || '';
      isActive = body.isActive !== undefined ? body.isActive : true;
      order = body.order || 0;
    }

    const newHeroBanner = await prisma.heroBanner.create({
      data: { title, subtitle, imageUrl, isActive, order },
    });

    return NextResponse.json(newHeroBanner);
  } catch (error) {
    console.error('Error creating hero banner:', error);
    return NextResponse.json(
      { error: 'Failed to create hero banner' },
      { status: 500 }
    );
  }
}

