import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import path from 'path';
import { promises as fs } from 'fs';

export const runtime = 'nodejs';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    const heroBanner = await prisma.heroBanner.findUnique({
      where: { id },
    });

    if (!heroBanner) {
      return NextResponse.json(
        { error: 'Hero banner not found' },
        { status: 404 }
      );
    }

    return NextResponse.json(heroBanner);
  } catch (error) {
    console.error('Error fetching hero banner:', error);
    return NextResponse.json(
      { error: 'Failed to fetch hero banner' },
      { status: 500 }
    );
  }
}

export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    const contentType = request.headers.get('content-type') || '';
    let title = '';
    let subtitle = '';
    let imageUrl: string | undefined = undefined;
    let isActive: boolean | undefined = undefined;
    let order: number | undefined = undefined;

    if (contentType.includes('multipart/form-data')) {
      const form = await request.formData();
      title = String(form.get('title') || '');
      subtitle = String(form.get('subtitle') || '');
      const isActiveRaw = form.get('isActive');
      if (isActiveRaw !== null) isActive = String(isActiveRaw) === 'true';
      const orderRaw = form.get('order');
      if (orderRaw !== null) order = parseInt(String(orderRaw)) || 0;
      const file = form.get('image') as File | null;
      if (file && file.size > 0) {
        const arrayBuffer = await file.arrayBuffer();
        const buffer = Buffer.from(arrayBuffer);
        const uploadsDir = path.join(process.cwd(), 'public', 'uploads');
        await fs.mkdir(uploadsDir, { recursive: true });
        const safeName = file.name.replace(/[^a-zA-Z0-9_.-]/g, '_');
        const filename = `${Date.now()}_${safeName}`;
        const outPath = path.join(uploadsDir, filename);
        await fs.writeFile(outPath, buffer);
        imageUrl = `/uploads/${filename}`;
      } else {
        const fromField = String(form.get('imageUrl') || '');
        if (fromField) imageUrl = fromField;
      }
    } else {
      const body = await request.json();
      title = body.title;
      subtitle = body.subtitle;
      imageUrl = body.imageUrl;
      isActive = body.isActive;
      order = body.order;
    }

    const data: any = { title, subtitle, updatedAt: new Date() };
    if (imageUrl !== undefined) data.imageUrl = imageUrl;
    if (isActive !== undefined) data.isActive = isActive;
    if (order !== undefined) data.order = order;

    const updatedHeroBanner = await prisma.heroBanner.update({
      where: { id },
      data,
    });

    return NextResponse.json(updatedHeroBanner);
  } catch (error) {
    console.error('Error updating hero banner:', error);
    return NextResponse.json(
      { error: 'Failed to update hero banner' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    await prisma.heroBanner.delete({
      where: { id },
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error deleting hero banner:', error);
    return NextResponse.json(
      { error: 'Failed to delete hero banner' },
      { status: 500 }
    );
  }
}

