"use client";

import { useEffect, useMemo, useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { toast } from "sonner";

type BankAccount = {
  id: string;
  bankName: string;
  accountNumber: string;
  accountName: string;
  order?: number;
};

export default function PaymentSettingsPage() {
  const [bankAccounts, setBankAccounts] = useState<BankAccount[]>([]);
  const [loading, setLoading] = useState(true);

  const [newAccount, setNewAccount] = useState<Omit<BankAccount, "id">>({
    bankName: "",
    accountNumber: "",
    accountName: "",
    order: 0,
  });

  const [editing, setEditing] = useState<Record<string, Partial<BankAccount>>>({});

  const [whatsappNumber, setWhatsappNumber] = useState("");
  const [savingWhatsApp, setSavingWhatsApp] = useState(false);

  const sortedAccounts = useMemo(
    () => [...bankAccounts].sort((a, b) => (a.order ?? 0) - (b.order ?? 0)),
    [bankAccounts]
  );

  useEffect(() => {
    const load = async () => {
      try {
        const [accRes, settingsRes] = await Promise.all([
          fetch("/api/bank-accounts"),
          fetch("/api/settings"),
        ]);
        const accData = await accRes.json();
        const settings = await settingsRes.json();
        if (Array.isArray(accData)) setBankAccounts(accData);
        if (settings && typeof settings.whatsappNumber === "string") {
          setWhatsappNumber(settings.whatsappNumber);
        }
      } catch (e) {
        toast.error("Gagal memuat data");
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const handleCreate = async () => {
    if (!newAccount.bankName || !newAccount.accountNumber || !newAccount.accountName) {
      toast.error("Lengkapi semua field rekening");
      return;
    }
    try {
      const res = await fetch("/api/bank-accounts", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newAccount),
      });
      if (!res.ok) throw new Error();
      const created = await res.json();
      setBankAccounts((prev) => [...prev, created]);
      setNewAccount({ bankName: "", accountNumber: "", accountName: "", order: 0 });
      toast.success("Rekening ditambahkan");
    } catch (e) {
      toast.error("Gagal menambah rekening");
    }
  };

  const handleUpdate = async (id: string) => {
    const payload = editing[id];
    if (!payload) return;
    try {
      const res = await fetch(`/api/bank-accounts/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      if (!res.ok) throw new Error();
      const updated = await res.json();
      setBankAccounts((prev) => prev.map((x) => (x.id === id ? updated : x)));
      setEditing((prev) => {
        const cp = { ...prev };
        delete cp[id];
        return cp;
      });
      toast.success("Rekening diperbarui");
    } catch (e) {
      toast.error("Gagal memperbarui rekening");
    }
  };

  const handleDelete = async (id: string) => {
    if (!confirm("Hapus rekening ini?")) return;
    try {
      const res = await fetch(`/api/bank-accounts/${id}`, { method: "DELETE" });
      if (!res.ok) throw new Error();
      setBankAccounts((prev) => prev.filter((x) => x.id !== id));
      toast.success("Rekening dihapus");
    } catch (e) {
      toast.error("Gagal menghapus rekening");
    }
  };

  const saveWhatsApp = async () => {
    setSavingWhatsApp(true);
    try {
      const res = await fetch("/api/settings", {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ whatsappNumber }),
      });
      if (!res.ok) throw new Error();
      toast.success("Nomor WhatsApp disimpan");
    } catch (e) {
      toast.error("Gagal menyimpan nomor WhatsApp");
    } finally {
      setSavingWhatsApp(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[200px]">Memuat...</div>
    );
  }

  return (
    <div className="space-y-8">
      <div>
        <h1 className="text-2xl font-semibold">Pengaturan Pembayaran</h1>
        <p className="text-sm text-muted-foreground">Kelola rekening tujuan dan nomor WhatsApp.</p>
      </div>

      <Card>
        <CardContent className="pt-6 space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="space-y-2">
              <Label>Nomor WhatsApp</Label>
              <Input
                placeholder="contoh: 628123456789"
                value={whatsappNumber}
                onChange={(e) => setWhatsappNumber(e.target.value)}
              />
            </div>
          </div>
          <div className="flex gap-2">
            <Button onClick={saveWhatsApp} disabled={savingWhatsApp}>
              {savingWhatsApp ? "Menyimpan..." : "Simpan"}
            </Button>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardContent className="pt-6 space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="space-y-2">
              <Label>Nama Bank</Label>
              <Input
                placeholder="BCA / Mandiri / BNI"
                value={newAccount.bankName}
                onChange={(e) => setNewAccount((s) => ({ ...s, bankName: e.target.value }))}
              />
            </div>
            <div className="space-y-2">
              <Label>Nomor Rekening</Label>
              <Input
                placeholder="1234567890"
                value={newAccount.accountNumber}
                onChange={(e) => setNewAccount((s) => ({ ...s, accountNumber: e.target.value }))}
              />
            </div>
            <div className="space-y-2">
              <Label>Nama Pemilik</Label>
              <Input
                placeholder="Travel Indonesia"
                value={newAccount.accountName}
                onChange={(e) => setNewAccount((s) => ({ ...s, accountName: e.target.value }))}
              />
            </div>
            <div className="space-y-2">
              <Label>Urutan</Label>
              <Input
                type="number"
                value={newAccount.order ?? 0}
                onChange={(e) => setNewAccount((s) => ({ ...s, order: Number(e.target.value) }))}
              />
            </div>
          </div>
          <div>
            <Button onClick={handleCreate}>Tambah Rekening</Button>
          </div>

          <div className="border-t pt-4 space-y-3">
            {sortedAccounts.length === 0 && (
              <div className="text-sm text-muted-foreground">Belum ada rekening.</div>
            )}
            {sortedAccounts.map((acc) => {
              const edit = editing[acc.id] ?? {};
              return (
                <div key={acc.id} className="grid grid-cols-1 md:grid-cols-5 gap-3 items-end">
                  <div className="space-y-1">
                    <Label>Bank</Label>
                    <Input
                      defaultValue={acc.bankName}
                      onChange={(e) =>
                        setEditing((s) => ({ ...s, [acc.id]: { ...s[acc.id], bankName: e.target.value } }))
                      }
                    />
                  </div>
                  <div className="space-y-1">
                    <Label>No. Rekening</Label>
                    <Input
                      defaultValue={acc.accountNumber}
                      onChange={(e) =>
                        setEditing((s) => ({ ...s, [acc.id]: { ...s[acc.id], accountNumber: e.target.value } }))
                      }
                    />
                  </div>
                  <div className="space-y-1">
                    <Label>Atas Nama</Label>
                    <Input
                      defaultValue={acc.accountName}
                      onChange={(e) =>
                        setEditing((s) => ({ ...s, [acc.id]: { ...s[acc.id], accountName: e.target.value } }))
                      }
                    />
                  </div>
                  <div className="space-y-1">
                    <Label>Urutan</Label>
                    <Input
                      type="number"
                      defaultValue={acc.order ?? 0}
                      onChange={(e) =>
                        setEditing((s) => ({ ...s, [acc.id]: { ...s[acc.id], order: Number(e.target.value) } }))
                      }
                    />
                  </div>
                  <div className="flex gap-2">
                    <Button variant="secondary" onClick={() => handleUpdate(acc.id)}>
                      Simpan
                    </Button>
                    <Button variant="destructive" onClick={() => handleDelete(acc.id)}>
                      Hapus
                    </Button>
                  </div>
                </div>
              );
            })}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

src/components/flights/flight-result-car.content:
'use client';

import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Heart, Plane, Star } from 'lucide-react';
import { formatRupiah } from '@/lib/format';
import { useState } from 'react';
import { useRouter } from 'next/navigation';
import { useTranslations } from 'next-intl';
import Image from 'next/image';

interface OtherFee {
  id: string;
  name: string;
  amount: number;
}

interface FlightResultCardProps {
  id: string;
  airline: string;
  departureCity: string;
  arrivalCity: string;
  departureTime: string;
  arrivalTime: string;
  duration: string;
  price: number;
  originalPrice?: number;
  rating: number;
  reviewCount: number;
  imageUrl?: string;
  otherFees?: OtherFee[];
  flightClass?: string;
  departureDate?: string;
}

export function FlightResultCard({
  id,
  airline,
  departureCity,
  arrivalCity,
  departureTime,
  arrivalTime,
  duration,
  price,
  originalPrice,
  rating,
  reviewCount,
  imageUrl,
  otherFees,
  flightClass,
  departureDate,
}: FlightResultCardProps) {
  const t = useTranslations('common');
  const router = useRouter();
  const [isFavorite, setIsFavorite] = useState(false);

  const formatTime = (value: string) => {
    try {
      // Handle ISO or time strings
      if (value.includes('T') || value.endsWith('Z')) {
        const d = new Date(value);
        const hh = d.getHours().toString().padStart(2, '0');
        const mm = d.getMinutes().toString().padStart(2, '0');
        return `${hh}:${mm}`;
      }
      // Already HH:mm or H:mm
      const match = value.match(/^(\d{1,2}):(\d{2})/);
      if (match) {
        const hh = match[1].padStart(2, '0');
        const mm = match[2];
        return `${hh}:${mm}`;
      }
      return value;
    } catch {
      return value;
    }
  };

  const dep = formatTime(departureTime);
  const arr = formatTime(arrivalTime);

  return (
    <Card className="overflow-hidden hover:shadow-lg transition-shadow px-1">
      <div className="flex flex-col md:flex-row h-full">
        {/* Image Section - Left */}
        <div className="relative w-full md:w-48 h-48 md:h-auto shrink-0">
          {imageUrl ? (
            <Image
              src={imageUrl}
              alt={airline}
              fill
              className="object-cover"
            />
          ) : (
            <div className="w-full h-full bg-muted flex items-center justify-center">
              <Plane className="w-16 h-16 text-muted-foreground" />
            </div>
          )}
        </div>

        {/* Content Section - Middle */}
        <div className="flex-1 p-1 md:p-6 flex flex-col justify-between">
          <div>
            <div className="flex items-center gap-2 text-sm text-muted-foreground mb-4">
              <Star className="h-4 w-4 fill-yellow-400 text-yellow-400" />
              <span className="font-semibold text-foreground">{rating?.toFixed ? rating.toFixed(1) : rating}</span>
              <span></span>
              <span>{t('reviews')}</span>
            </div>

            <div className="mb-4">
              <div className="text-2xl md:text-3xl font-bold text-foreground mb-2">
                {dep} - {arr}
              </div>
              <div className="text-base text-muted-foreground">{airline}</div>
            </div>
          </div>

          <div className="space-y-2">
            <div className="text-sm text-muted-foreground">
              {departureCity} - {arrivalCity}
            </div>
            {departureDate && (
              <div className="text-xs text-muted-foreground">
                {t('date')}: {new Date(departureDate).toLocaleDateString('id-ID', {
                  day: 'numeric',
                  month: 'long',
                  year: 'numeric'
                })}
              </div>
            )}
            {flightClass && (
              <div className="text-xs font-medium text-muted-foreground capitalize">
                {t('type')}: {flightClass}
              </div>
            )}
            {otherFees && otherFees.length > 0 && (
              <div className="text-xs text-muted-foreground">
                {t('included')}: {otherFees.map(fee => fee.name).join(', ')}
              </div>
            )}
          </div>
        </div>

        {/* Price & Action Section - Right */}
        <div className="flex flex-row md:flex-col justify-between items-center md:items-end p-5 md:p-6 md:min-w-[220px] border-t md:border-t-0 md:border-l">
          <button
            onClick={() => setIsFavorite(!isFavorite)}
            className="p-2 rounded-full border border-border hover:bg-muted md:mb-auto"
            aria-label="toggle favorite"
          >
            <Heart
              className={`w-5 h-5 ${isFavorite ? 'fill-destructive text-destructive' : 'text-muted-foreground'}`}
            />
          </button>

          <div className="flex flex-col items-end gap-4 md:mt-auto">
            <div className="text-right">
              <div className="text-xs text-muted-foreground mb-2">{t('startingFrom')}</div>
              {originalPrice && (
                <div className="text-sm line-through text-muted-foreground/70 mb-1">
                  {formatRupiah(originalPrice)}
                </div>
              )}
              <div className="text-2xl md:text-3xl font-bold text-primary">
                {formatRupiah(price)}
              </div>
            </div>
            <Button 
              onClick={() => router.push(`/flights/${id}/book`)}
              className="w-full md:w-auto md:min-w-[160px] bg-primary text-primary-foreground hover:bg-primary/90 px-6"
              size="lg"
            >
              {t('viewDeals')}
            </Button>
          </div>
        </div>
      </div>
    </Card>
  );
}

src/app/api/popular-flight-routes/[id]/r.content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/lib/prisma';

export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;
    const body = await request.json();

    const popularRoute = await db.popularFlightRoute.update({
      where: { id },
      data: {
        displayOrder: body.displayOrder,
        isActive: body.isActive,
      },
      include: {
        flightRoute: {
          include: {
            departureCity: true,
            arrivalCity: true,
          },
        },
      },
    });

    return NextResponse.json(popularRoute);
  } catch (error) {
    console.error('Error updating popular flight route:', error);
    return NextResponse.json(
      { error: 'Failed to update popular flight route' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const { id } = await params;

    await db.popularFlightRoute.delete({
      where: { id },
    });

    return NextResponse.json({ message: 'Popular flight route deleted successfully' });
  } catch (error) {
    console.error('Error deleting popular flight route:', error);
    return NextResponse.json(
      { error: 'Failed to delete popular flight route' },
      { status: 500 }
    );
  }
}

src/app/api/popular-flight-routes/route..content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/lib/prisma';

export async function GET() {
  try {
    const popularRoutes = await db.popularFlightRoute.findMany({
      where: {
        isActive: true,
      },
      include: {
        flightRoute: {
          include: {
            departureCity: true,
            arrivalCity: true,
          },
        },
      },
      orderBy: {
        displayOrder: 'asc',
      },
    });

    // Map totalPrice to price for frontend compatibility
    const mappedRoutes = popularRoutes.map(route => ({
      ...route,
      flightRoute: {
        ...route.flightRoute,
        price: route.flightRoute.totalPrice,
      },
    }));

    return NextResponse.json(mappedRoutes);
  } catch (error) {
    console.error('Error fetching popular flight routes:', error);
    return NextResponse.json(
      { error: 'Failed to fetch popular flight routes' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();

    const existingPopular = await db.popularFlightRoute.findUnique({
      where: {
        flightRouteId: body.flightRouteId,
      },
    });

    if (existingPopular) {
      return NextResponse.json(
        { error: 'This flight route is already marked as popular' },
        { status: 400 }
      );
    }

    const popularRoute = await db.popularFlightRoute.create({
      data: {
        flightRouteId: body.flightRouteId,
        displayOrder: body.displayOrder || 0,
        isActive: body.isActive !== undefined ? body.isActive : true,
        imageUrl: body.imageUrl || null,
      },
      include: {
        flightRoute: {
          include: {
            departureCity: true,
            arrivalCity: true,
          },
        },
      },
    });

    // Map totalPrice to price for frontend compatibility
    const mappedRoute = {
      ...popularRoute,
      flightRoute: {
        ...popularRoute.flightRoute,
        price: popularRoute.flightRoute.totalPrice,
      },
    };

    return NextResponse.json(mappedRoute, { status: 201 });
  } catch (error) {
    console.error('Error creating popular flight route:', error);
    return NextResponse.json(
      { error: 'Failed to create popular flight route' },
      { status: 500 }
    );
  }
}

src/app/api/upload/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { writeFile } from 'fs/promises';
import { join } from 'path';
import { existsSync, mkdirSync } from 'fs';

export async function POST(request: NextRequest) {
  try {
    const formData = await request.formData();
    const file = formData.get('file') as File;

    if (!file) {
      return NextResponse.json(
        { error: 'No file uploaded' },
        { status: 400 }
      );
    }

    // Validate file type
    const validTypes = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/gif'];
    if (!validTypes.includes(file.type)) {
      return NextResponse.json(
        { error: 'Invalid file type. Only images are allowed' },
        { status: 400 }
      );
    }

    // Validate file size (max 5MB)
    const maxSize = 5 * 1024 * 1024; // 5MB
    if (file.size > maxSize) {
      return NextResponse.json(
        { error: 'File size too large. Maximum 5MB allowed' },
        { status: 400 }
      );
    }

    const bytes = await file.arrayBuffer();
    const buffer = Buffer.from(bytes);

    // Generate unique filename
    const timestamp = Date.now();
    const originalName = file.name.replace(/[^a-zA-Z0-9.-]/g, '_');
    const filename = `${timestamp}-${originalName}`;

    // Use external uploads directory (outside Next.js public folder)
    // This prevents issues with PM2 where files uploaded after build aren't accessible
    const uploadsDir = process.env.UPLOADS_DIR || join(process.cwd(), 'uploads');
    if (!existsSync(uploadsDir)) {
      mkdirSync(uploadsDir, { recursive: true });
    }

    // Save file
    const filepath = join(uploadsDir, filename);
    await writeFile(filepath, buffer);

    // Return API URL that serves the image
    const url = `/api/images/${filename}`;

    return NextResponse.json({
      success: true,
      url,
      filename,
    });
  } catch (error) {
    console.error('Error uploading file:', error);
    return NextResponse.json(
      { error: 'Failed to upload file' },
      { status: 500 }
    );
  }
}

src/components/admin/dynamic-fare-form.t.content:
'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Plus, Trash2 } from 'lucide-react';

interface FareItem {
  id: string;
  name: string;
  amount: string;
}

interface DynamicFareFormProps {
  baseFare: string;
  tax: string;
  discount: string;
  otherFees: FareItem[];
  onBaseFareChange: (value: string) => void;
  onTaxChange: (value: string) => void;
  onDiscountChange: (value: string) => void;
  onOtherFeesChange: (fees: FareItem[]) => void;
}

export default function DynamicFareForm({
  baseFare,
  tax,
  discount,
  otherFees,
  onBaseFareChange,
  onTaxChange,
  onDiscountChange,
  onOtherFeesChange,
}: DynamicFareFormProps) {
  const addFeeItem = () => {
    const newFee: FareItem = {
      id: `fee-${Date.now()}`,
      name: '',
      amount: '0',
    };
    onOtherFeesChange([...otherFees, newFee]);
  };

  const removeFeeItem = (id: string) => {
    onOtherFeesChange(otherFees.filter(fee => fee.id !== id));
  };

  const updateFeeItem = (id: string, field: 'name' | 'amount', value: string) => {
    onOtherFeesChange(
      otherFees.map(fee =>
        fee.id === id ? { ...fee, [field]: value } : fee
      )
    );
  };

  const calculateTotal = () => {
    const base = parseFloat(baseFare) || 0;
    const taxAmt = parseFloat(tax) || 0;
    const disc = parseFloat(discount) || 0;
    const otherTotal = otherFees.reduce((sum, fee) => sum + (parseFloat(fee.amount) || 0), 0);
    return base + taxAmt + otherTotal - disc;
  };

  const formatRupiah = (price: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(price);
  };

  return (
    <div className="space-y-4">
      <h3 className="font-semibold text-lg">Rincian Tarif</h3>

      <div className="grid grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="baseFare">Tarif Dasar (Rp)</Label>
          <Input
            type="number"
            id="baseFare"
            min="0"
            value={baseFare}
            onChange={(e) => onBaseFareChange(e.target.value)}
            required
          />
        </div>
        <div className="space-y-2">
          <Label htmlFor="tax">Pajak (Rp)</Label>
          <Input
            type="number"
            id="tax"
            min="0"
            value={tax}
            onChange={(e) => onTaxChange(e.target.value)}
            required
          />
        </div>
      </div>

      <div className="space-y-2">
        <div className="flex justify-between items-center">
          <Label>Biaya Tambahan</Label>
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={addFeeItem}
          >
            <Plus className="w-4 h-4 mr-1" />
            Tambah Biaya
          </Button>
        </div>

        {otherFees.length === 0 ? (
          <div className="text-center py-4 text-gray-500 border-2 border-dashed rounded-lg">
            Belum ada biaya tambahan. Klik "Tambah Biaya" untuk menambahkan.
          </div>
        ) : (
          <div className="space-y-2 max-h-60 overflow-y-auto border rounded-lg p-3">
            {otherFees.map((fee) => (
              <div key={fee.id} className="flex gap-2 items-end">
                <div className="flex-1 space-y-1">
                  <Label className="text-xs">Nama Biaya</Label>
                  <Input
                    type="text"
                    placeholder="Contoh: Biaya WiFi"
                    value={fee.name}
                    onChange={(e) => updateFeeItem(fee.id, 'name', e.target.value)}
                    required
                  />
                </div>
                <div className="w-40 space-y-1">
                  <Label className="text-xs">Jumlah (Rp)</Label>
                  <Input
                    type="number"
                    min="0"
                    placeholder="0"
                    value={fee.amount}
                    onChange={(e) => updateFeeItem(fee.id, 'amount', e.target.value)}
                    required
                  />
                </div>
                <Button
                  type="button"
                  variant="outline"
                  size="icon"
                  onClick={() => removeFeeItem(fee.id)}
                  className="flex-shrink-0"
                >
                  <Trash2 className="w-4 h-4" />
                </Button>
              </div>
            ))}
          </div>
        )}
      </div>

      <div className="space-y-2">
        <Label htmlFor="discount">Diskon (Rp)</Label>
        <Input
          type="number"
          id="discount"
          min="0"
          value={discount}
          onChange={(e) => onDiscountChange(e.target.value)}
        />
      </div>

      <div className="p-4 bg-gradient-to-r from-primary/10 to-primary/20 rounded-lg border-2 border-primary/30">
        <div className="space-y-2">
          <div className="flex justify-between text-sm">
            <span>Tarif Dasar:</span>
            <span>{formatRupiah(parseFloat(baseFare) || 0)}</span>
          </div>
          <div className="flex justify-between text-sm">
            <span>Pajak:</span>
            <span>{formatRupiah(parseFloat(tax) || 0)}</span>
          </div>
          {otherFees.map((fee) => (
            <div key={fee.id} className="flex justify-between text-sm">
              <span>{fee.name || 'Biaya Tambahan'}:</span>
              <span>{formatRupiah(parseFloat(fee.amount) || 0)}</span>
            </div>
          ))}
          {parseFloat(discount) > 0 && (
            <div className="flex justify-between text-sm text-red-600">
              <span>Diskon:</span>
              <span>-{formatRupiah(parseFloat(discount) || 0)}</span>
            </div>
          )}
          <div className="flex justify-between items-center pt-2 border-t border-primary/30">
            <span className="font-semibold text-lg">Total Harga:</span>
            <span className="font-bold text-2xl text-primary">
              {formatRupiah(calculateTotal())}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}

src/app/flights/[id]/book/page.tsx.content:
'use client';

import { useState, useEffect, use } from 'react';
import { useRouter } from 'next/navigation';
import { useTranslations } from 'next-intl';
import HeaderNavigation from "@/components/sections/header-navigation";
import Footer from "@/components/sections/footer";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { ArrowLeft, Plane, Heart, Share2 } from 'lucide-react';
import { formatRupiah } from '@/lib/format';
import FlightPaymentModal from '@/components/flights/flight-payment-modal';
import { getCurrentUser, redirectToSSO } from '@/lib/sso-auth';

interface OtherFee {
  id: string;
  name: string;
  amount: number;
}

interface FlightRoute {
  id: string;
  departureCityId: string;
  arrivalCityId: string;
  departureDate: string;
  returnDate: string | null;
  price: number;
  airline: string;
  departureTime: string;
  arrivalTime: string;
  duration: string;
  rating: number;
  availableSeats: number;
  flightClass: string;
  baseFare: number;
  tax: number;
  serviceFee: number;
  otherFees?: OtherFee[];
  discount: number;
  totalPrice: number;
  departureCity: { id: string; name: string };
  arrivalCity: { id: string; name: string };
}

export default function FlightBookingPage({
  params,
}: {
  params: Promise<{ id: string }>;
}) {
  const t = useTranslations('flights');
  const tCommon = useTranslations('common');
  const router = useRouter();
  const resolvedParams = use(params);
  const [flightRoute, setFlightRoute] = useState<FlightRoute | null>(null);
  const [loading, setLoading] = useState(true);
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    fetchFlightRoute();
    checkLoginStatus();
  }, [resolvedParams.id]);

  const checkLoginStatus = () => {
    const user = getCurrentUser();
    setIsLoggedIn(!!user);
  };

  const handlePayNowClick = () => {
    if (!isLoggedIn) {
      const currentUrl = window.location.pathname;
      redirectToSSO(currentUrl);
    } else {
      setShowPaymentModal(true);
    }
  };

  const fetchFlightRoute = async () => {
    try {
      const response = await fetch(`/api/flight-routes/${resolvedParams.id}`);
      const data = await response.json();
      setFlightRoute(data);
    } catch (error) {
      console.error('Error fetching flight route:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatTime = (timeString: string) => {
    try {
      if (timeString.includes('T') || timeString.endsWith('Z')) {
        const date = new Date(timeString);
        return date.toLocaleTimeString('en-US', { 
          hour: '2-digit', 
          minute: '2-digit',
          hour12: false 
        });
      }
      return timeString;
    } catch {
      return timeString;
    }
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const calculateSubtotal = (fees?: OtherFee[]) => {
    if (!fees || fees.length === 0) return 0;
    return fees.reduce((sum, fee) => sum + Number(fee.amount), 0);
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg">{t('loading')}</div>
      </div>
    );
  }

  if (!flightRoute) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-lg">{t('flightNotFound')}</div>
      </div>
    );
  }

  const subtotal = calculateSubtotal(flightRoute.otherFees);
  const discountAmount = Number(flightRoute.discount) || 0;
  const originalPrice = subtotal;

  return (
    <div className="min-h-screen bg-background">
      <HeaderNavigation mode="page" />

      <main className="container mx-auto px-4 sm:px-6 lg:px-20 py-8 mt-16">
        <Button
          variant="ghost"
          onClick={() => router.back()}
          className="mb-6"
        >
          <ArrowLeft className="w-4 h-4 mr-2" />
          {t('back')}
        </Button>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2 space-y-6">
            {/* Flight Details Card */}
            <Card>
              <CardContent className="pt-6">
                {/* Departure Date */}
                <div className="flex items-center justify-between mb-6">
                  <h3 className="font-semibold text-lg">
                    {t('departureDate')}: {formatDate(flightRoute.departureDate)}
                  </h3>
                  <span className="text-sm text-muted-foreground">
                    {t('duration')}: {flightRoute.duration}
                  </span>
                </div>

                {/* Airline Info */}
                <div className="flex items-center gap-4 mb-8 p-4 bg-muted/50 rounded-lg">
                  <div className="w-16 h-16 bg-white rounded-lg flex items-center justify-center border">
                    <Plane className="w-8 h-8 text-primary" />
                  </div>
                  <div>
                    <h4 className="font-bold text-lg">{flightRoute.airline}</h4>
                    <p className="text-sm text-muted-foreground">
                      {flightRoute.flightClass === 'economy' ? t('economyClass') : flightRoute.flightClass === 'business' ? t('businessClass') : t('firstClass')}
                    </p>
                  </div>
                </div>

                {/* Flight Route */}
                <div className="relative">
                  <div className="flex items-start justify-between">
                    {/* Departure */}
                    <div className="flex-1">
                      <p className="text-2xl font-bold">
                        {formatTime(flightRoute.departureTime)}
                      </p>
                      <p className="text-sm mt-1">{formatDate(flightRoute.departureDate)}</p>
                      <p className="font-semibold mt-2">{flightRoute.departureCity.name}</p>
                    </div>

                    {/* Flight Icon */}
                    <div className="flex flex-col items-center px-8 py-4">
                      <div className="flex items-center gap-2">
                        <div className="h-px w-20 bg-border"></div>
                        <Plane className="w-6 h-6 rotate-90 text-primary" />
                        <div className="h-px w-20 bg-border"></div>
                      </div>
                    </div>

                    {/* Arrival */}
                    <div className="flex-1 text-right">
                      <p className="text-2xl font-bold">
                        {formatTime(flightRoute.arrivalTime)}
                      </p>
                      <p className="text-sm mt-1">{formatDate(flightRoute.departureDate)}</p>
                      <p className="font-semibold mt-2">{flightRoute.arrivalCity.name}</p>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>

            {/* Fare Details Card */}
            <Card>
              <CardHeader>
                <CardTitle>{t('fareDetails')}</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="p-4 bg-primary/10 dark:bg-primary/20 rounded-lg">
                  <div className="flex justify-between items-center">
                    <span className="font-semibold">{t('passengerType')}: {t('adult')}</span>
                  </div>
                  <div className="mt-2">
                    <span className="text-sm font-semibold">1 {t('traveler')}</span>
                  </div>
                </div>

                <div className="space-y-3">
                  {flightRoute.otherFees && flightRoute.otherFees.length > 0 ? (
                    <>
                      {flightRoute.otherFees.map((fee, index) => (
                        <div key={fee.id}>
                          <div className="flex justify-between">
                            <span className="text-muted-foreground">{fee.name}</span>
                            <div className="text-right">
                              <span className="font-medium">{formatRupiah(Number(fee.amount))}</span>
                            </div>
                          </div>
                          {index < flightRoute.otherFees!.length - 1 && <Separator className="my-3" />}
                        </div>
                      ))}
                    </>
                  ) : (
                    <div className="text-center py-4 text-muted-foreground">
                      <p className="text-sm">{t('noFareDetails')}</p>
                    </div>
                  )}

                  <Separator />

                  <div className="flex justify-between font-semibold">
                    <span>{t('subtotal')}</span>
                    <span>{formatRupiah(Number(flightRoute.totalPrice))}</span>
                  </div>

                  <Separator className="my-4" />

                  <div className="flex justify-between text-lg font-bold">
                    <span>{t('total')}</span>
                    <span className="text-primary">{formatRupiah(Number(flightRoute.totalPrice))}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            <Card className="sticky top-24">
              <CardContent className="pt-6 space-y-4">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-muted-foreground">{flightRoute.flightClass.charAt(0).toUpperCase() + flightRoute.flightClass.slice(1)} Class</p>
                    {discountAmount > 0 && (
                      <p className="text-lg line-through text-muted-foreground/70">
                        {formatRupiah(originalPrice)}
                      </p>
                    )}
                    <p className="text-3xl font-bold text-primary">
                      {formatRupiah(Number(flightRoute.totalPrice))}
                    </p>
                  </div>
                  <div className="flex gap-2">
                    <Button
                      size="icon"
                      variant="outline"
                      onClick={() => setIsFavorite(!isFavorite)}
                    >
                      <Heart 
                        className={`w-5 h-5 ${isFavorite ? 'fill-red-500 text-red-500' : ''}`} 
                      />
                    </Button>
                    <Button size="icon" variant="outline">
                      <Share2 className="w-5 h-5" />
                    </Button>
                  </div>
                </div>

                <Button
                  size="lg"
                  className="w-full"
                  onClick={handlePayNowClick}
                >
                  {isLoggedIn ? t('payNow') : 'Pay Now'}
                </Button>

                <div className="pt-4 border-t space-y-3 text-sm">
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t('route')}</span>
                    <span className="font-medium text-right">
                      {flightRoute.departureCity.name}  {flightRoute.arrivalCity.name}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t('duration')}</span>
                    <span className="font-medium">{flightRoute.duration}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t('airline')}</span>
                    <span className="font-medium">{flightRoute.airline}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-muted-foreground">{t('class')}</span>
                    <span className="font-medium">{flightRoute.flightClass.charAt(0).toUpperCase() + flightRoute.flightClass.slice(1)}</span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </main>

      <Footer />

      {flightRoute && (
        <FlightPaymentModal
          isOpen={showPaymentModal}
          onClose={() => setShowPaymentModal(false)}
          flightRoute={flightRoute}
        />
      )}
    </div>
  );
}

src/app/api/images/[filename]/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { readFile } from 'fs/promises';
import { join } from 'path';
import { existsSync } from 'fs';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ filename: string }> }
) {
  try {
    const { filename } = await params;

    // Security: Prevent directory traversal attacks
    if (filename.includes('..') || filename.includes('/') || filename.includes('\\')) {
      return NextResponse.json(
        { error: 'Invalid filename' },
        { status: 400 }
      );
    }

    // Get uploads directory
    const uploadsDir = process.env.UPLOADS_DIR || join(process.cwd(), 'uploads');
    const filepath = join(uploadsDir, filename);

    // Check if file exists
    if (!existsSync(filepath)) {
      return NextResponse.json(
        { error: 'File not found' },
        { status: 404 }
      );
    }

    // Read file
    const fileBuffer = await readFile(filepath);

    // Determine content type based on file extension
    const ext = filename.split('.').pop()?.toLowerCase();
    const contentTypeMap: Record<string, string> = {
      'jpg': 'image/jpeg',
      'jpeg': 'image/jpeg',
      'png': 'image/png',
      'gif': 'image/gif',
      'webp': 'image/webp',
    };
    const contentType = contentTypeMap[ext || ''] || 'application/octet-stream';

    // Return image with proper headers
    return new NextResponse(fileBuffer, {
      headers: {
        'Content-Type': contentType,
        'Cache-Control': 'public, max-age=31536000, immutable',
      },
    });
  } catch (error) {
    console.error('Error serving image:', error);
    return NextResponse.json(
      { error: 'Failed to serve image' },
      { status: 500 }
    );
  }
}

src/components/flights/flight-payment-mo.content:
'use client';

import { useState, useEffect } from 'react';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Copy, Check, CreditCard, Building2, Plane } from 'lucide-react';
import { toast } from 'sonner';

interface FlightRoute {
  id: string;
  departureCityId: string;
  arrivalCityId: string;
  departureDate: string;
  returnDate: string | null;
  price: number;
  airline: string;
  departureTime: string;
  arrivalTime: string;
  duration: string;
  rating: number;
  availableSeats: number;
  departureCity: { id: string; name: string };
  arrivalCity: { id: string; name: string };
}

interface FlightPaymentModalProps {
  isOpen: boolean;
  onClose: () => void;
  flightRoute: FlightRoute;
}

export default function FlightPaymentModal({ isOpen, onClose, flightRoute }: FlightPaymentModalProps) {
  const [copiedField, setCopiedField] = useState<string | null>(null);
  const [bankAccounts, setBankAccounts] = useState<Array<{
    id: string;
    bankName: string;
    accountNumber: string;
    accountName: string;
  }>>([]);
  const [whatsappNumber, setWhatsappNumber] = useState<string>("");
  const [paymentCreated, setPaymentCreated] = useState(false);

  useEffect(() => {
    if (isOpen && !paymentCreated) {
      createPaymentRecord();
    }
  }, [isOpen]);

  useEffect(() => {
    fetch('/api/bank-accounts')
      .then(res => res.json())
      .then(data => {
        if (Array.isArray(data)) {
          setBankAccounts(data);
        }
      })
      .catch(error => console.error('Error fetching bank accounts:', error));
  }, []);

  useEffect(() => {
    fetch('/api/settings')
      .then(res => res.json())
      .then(data => {
        if (data && typeof data.whatsappNumber === 'string') {
          setWhatsappNumber(data.whatsappNumber);
        }
      })
      .catch(error => console.error('Error fetching settings:', error));
  }, []);

  const createPaymentRecord = async () => {
    try {
      const userRes = await fetch('/api/auth/me');
      const userData = await userRes.json();

      if (!userData.user) {
        toast.error('Silakan login terlebih dahulu');
        return;
      }

      const response = await fetch('/api/payments', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          flightRouteId: flightRoute.id,
          userId: userData.user.id,
          userEmail: userData.user.email,
          userName: userData.user.name || userData.user.username || userData.user.email,
          amount: flightRoute.price,
        }),
      });

      if (response.ok) {
        setPaymentCreated(true);
      }
    } catch (error) {
      console.error('Error creating payment:', error);
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(price);
  };

  const handleCopy = (text: string, field: string) => {
    navigator.clipboard.writeText(text);
    setCopiedField(field);
    toast.success('Berhasil disalin!');
    setTimeout(() => setCopiedField(null), 2000);
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl flex items-center gap-2">
            <CreditCard className="w-6 h-6" />
            Informasi Pembayaran
          </DialogTitle>
          <DialogDescription>
            Transfer ke salah satu rekening di bawah ini dengan nominal yang sesuai
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6 py-4">
          <Card className="bg-primary/5">
            <CardContent className="pt-6">
              <div className="text-center space-y-2">
                <p className="text-sm text-muted-foreground">Total Pembayaran</p>
                <p className="text-4xl font-bold text-primary">{formatPrice(Number(flightRoute.price))}</p>
                <div className="flex items-center justify-center gap-2 text-sm text-muted-foreground">
                  <Plane className="w-4 h-4" />
                  <span>
                    {flightRoute.departureCity.name}  {flightRoute.arrivalCity.name}  {flightRoute.airline}
                  </span>
                </div>
                <p className="text-xs text-muted-foreground">
                  {new Date(flightRoute.departureDate).toLocaleDateString('id-ID', {
                    weekday: 'long',
                    year: 'numeric',
                    month: 'long',
                    day: 'numeric',
                  })}
                </p>
              </div>
            </CardContent>
          </Card>

          <div className="space-y-4">
            <div className="flex items-center gap-2 text-sm font-semibold">
              <Building2 className="w-4 h-4" />
              <span>Pilih Rekening Tujuan:</span>
            </div>

            {bankAccounts.map((account, index) => (
              <Card key={index} className="border-2 hover:border-primary transition-colors">
                <CardContent className="pt-6">
                  <div className="space-y-3">
                    <div className="flex items-center justify-between">
                      <span className="text-lg font-bold">{account.bankName}</span>
                      <span className="text-sm text-muted-foreground">
                        {account.accountName}
                      </span>
                    </div>

                    <div className="flex items-center justify-between bg-muted p-3 rounded-lg">
                      <span className="font-mono font-semibold text-lg">
                        {account.accountNumber}
                      </span>
                      <Button
                        size="sm"
                        variant="ghost"
                        onClick={() =>
                          handleCopy(account.accountNumber, `${account.bankName}-number`)
                        }
                      >
                        {copiedField === `${account.bankName}-number` ? (
                          <Check className="w-4 h-4" style={{ color: '#dcac56' }} />
                        ) : (
                          <Copy className="w-4 h-4" />
                        )}
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {whatsappNumber && (
            <Card className="bg-primary/10 dark:bg-primary/20 border-primary/30 dark:border-primary/50">
              <CardContent className="pt-6">
                <p className="text-sm">
                  Setelah melakukan pembayaran, silakan hubungi kami melalui WhatsApp di nomor{' '}
                  <a
                    className="font-semibold text-primary underline"
                    href={`https://wa.me/${whatsappNumber.replace(/\D/g, '')}`}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {whatsappNumber}
                  </a>
                  {' '}untuk konfirmasi pembayaran tiket penerbangan Anda.
                </p>
              </CardContent>
            </Card>
          )}

          <Card className="bg-blue-50 dark:bg-blue-950/20 border-blue-200 dark:border-blue-800">
            <CardContent className="pt-6">
              <div className="space-y-2 text-sm">
                <p className="font-semibold">Catatan Penting:</p>
                <ul className="list-disc list-inside space-y-1 text-muted-foreground">
                  <li>Transfer sesuai dengan nominal yang tertera</li>
                  <li>Simpan bukti transfer untuk konfirmasi</li>
                  <li>Pembayaran akan diverifikasi dalam 1x24 jam</li>
                  <li>Tiket akan dikirimkan setelah pembayaran diverifikasi</li>
                  <li>Hubungi CS kami jika ada kendala</li>
                </ul>
              </div>
            </CardContent>
          </Card>
        </div>

        <DialogFooter>
          <Button type="button" onClick={onClose} className="w-full">
            Tutup
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

