'use client';

import { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Input } from '@/components/ui/input';
import { toast } from 'sonner';
import { Plus, Trash2, ArrowUp, ArrowDown, Star, Upload, Image as ImageIcon } from 'lucide-react';
import { Switch } from '@/components/ui/switch';
import Image from 'next/image';

interface City {
  id: string;
  name: string;
}

interface FlightRoute {
  id: string;
  airline: string;
  departureCity: City;
  arrivalCity: City;
  totalPrice: number;
  departureDate: string;
  duration: string;
}

interface PopularFlightRoute {
  id: string;
  flightRouteId: string;
  displayOrder: number;
  isActive: boolean;
  imageUrl?: string | null;
  flightRoute: FlightRoute;
}

export default function PopularRoutesPage() {
  const [popularRoutes, setPopularRoutes] = useState<PopularFlightRoute[]>([]);
  const [allRoutes, setAllRoutes] = useState<FlightRoute[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [selectedRouteId, setSelectedRouteId] = useState<string>('');
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string>('');
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchPopularRoutes();
    fetchAllRoutes();
  }, []);

  const fetchPopularRoutes = async () => {
    try {
      const response = await fetch('/api/popular-flight-routes');
      const data = await response.json();
      setPopularRoutes(data);
    } catch (error) {
      console.error('Error fetching popular routes:', error);
      toast.error('Gagal memuat data rute populer');
    }
  };

  const fetchAllRoutes = async () => {
    try {
      const response = await fetch('/api/flight-routes');
      const data = await response.json();
      setAllRoutes(data);
    } catch (error) {
      console.error('Error fetching all routes:', error);
      toast.error('Gagal memuat data rute penerbangan');
    }
  };

  const handleImageSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setSelectedImage(file);
      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const uploadImage = async (): Promise<string | null> => {
    if (!selectedImage) return null;

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('file', selectedImage);

      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        return data.url;
      } else {
        toast.error('Gagal upload gambar');
        return null;
      }
    } catch (error) {
      console.error('Error uploading image:', error);
      toast.error('Gagal upload gambar');
      return null;
    } finally {
      setUploading(false);
    }
  };

  const handleAdd = async () => {
    if (!selectedRouteId) {
      toast.error('Pilih rute penerbangan terlebih dahulu');
      return;
    }

    try {
      // Upload image first if selected
      let imageUrl: string | null = null;
      if (selectedImage) {
        imageUrl = await uploadImage();
        if (!imageUrl) {
          return; // Stop if image upload failed
        }
      }

      const maxOrder = popularRoutes.length > 0
        ? Math.max(...popularRoutes.map(r => r.displayOrder))
        : -1;

      const response = await fetch('/api/popular-flight-routes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          flightRouteId: selectedRouteId,
          displayOrder: maxOrder + 1,
          isActive: true,
          imageUrl,
        }),
      });

      if (response.ok) {
        toast.success('Rute berhasil ditambahkan ke populer');
        setIsOpen(false);
        setSelectedRouteId('');
        setSelectedImage(null);
        setImagePreview('');
        fetchPopularRoutes();
      } else {
        const error = await response.json();
        toast.error(error.error || 'Gagal menambahkan rute populer');
      }
    } catch (error) {
      console.error('Error adding popular route:', error);
      toast.error('Gagal menambahkan rute populer');
    }
  };

  const handleDelete = async (id: string) => {
    if (!confirm('Apakah Anda yakin ingin menghapus rute ini dari populer?')) {
      return;
    }

    try {
      const response = await fetch(`/api/popular-flight-routes/${id}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Rute berhasil dihapus dari populer');
        fetchPopularRoutes();
      } else {
        throw new Error('Failed to delete popular route');
      }
    } catch (error) {
      console.error('Error deleting popular route:', error);
      toast.error('Gagal menghapus rute populer');
    }
  };

  const handleToggleActive = async (id: string, currentStatus: boolean) => {
    try {
      const route = popularRoutes.find(r => r.id === id);
      if (!route) return;

      const response = await fetch(`/api/popular-flight-routes/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          displayOrder: route.displayOrder,
          isActive: !currentStatus,
        }),
      });

      if (response.ok) {
        toast.success(`Rute ${!currentStatus ? 'diaktifkan' : 'dinonaktifkan'}`);
        fetchPopularRoutes();
      } else {
        throw new Error('Failed to toggle route');
      }
    } catch (error) {
      console.error('Error toggling route:', error);
      toast.error('Gagal mengubah status rute');
    }
  };

  const handleMoveUp = async (id: string, currentOrder: number) => {
    const previousRoute = popularRoutes.find(r => r.displayOrder === currentOrder - 1);
    if (!previousRoute) return;

    try {
      await fetch(`/api/popular-flight-routes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          displayOrder: currentOrder - 1,
          isActive: popularRoutes.find(r => r.id === id)?.isActive,
        }),
      });

      await fetch(`/api/popular-flight-routes/${previousRoute.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          displayOrder: currentOrder,
          isActive: previousRoute.isActive,
        }),
      });

      toast.success('Urutan berhasil diubah');
      fetchPopularRoutes();
    } catch (error) {
      console.error('Error moving route:', error);
      toast.error('Gagal mengubah urutan');
    }
  };

  const handleMoveDown = async (id: string, currentOrder: number) => {
    const nextRoute = popularRoutes.find(r => r.displayOrder === currentOrder + 1);
    if (!nextRoute) return;

    try {
      await fetch(`/api/popular-flight-routes/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          displayOrder: currentOrder + 1,
          isActive: popularRoutes.find(r => r.id === id)?.isActive,
        }),
      });

      await fetch(`/api/popular-flight-routes/${nextRoute.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          displayOrder: currentOrder,
          isActive: nextRoute.isActive,
        }),
      });

      toast.success('Urutan berhasil diubah');
      fetchPopularRoutes();
    } catch (error) {
      console.error('Error moving route:', error);
      toast.error('Gagal mengubah urutan');
    }
  };

  const formatRupiah = (price: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(price);
  };

  const availableRoutes = allRoutes.filter(
    route => !popularRoutes.some(pr => pr.flightRouteId === route.id)
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Rute Populer</h1>
          <p className="text-gray-600">Kelola rute penerbangan yang ditampilkan di halaman utama</p>
        </div>
        <Dialog open={isOpen} onOpenChange={setIsOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="w-4 h-4 mr-2" />
              Tambah Rute Populer
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Tambah Rute ke Populer</DialogTitle>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="route">Pilih Rute Penerbangan</Label>
                <Select value={selectedRouteId} onValueChange={setSelectedRouteId}>
                  <SelectTrigger>
                    <SelectValue placeholder="Pilih rute" />
                  </SelectTrigger>
                  <SelectContent>
                    {availableRoutes.map((route) => (
                      <SelectItem key={route.id} value={route.id}>
                        {route.departureCity.name}  {route.arrivalCity.name} ({route.airline}) - {formatRupiah(Number(route.totalPrice))}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Upload Gambar Destinasi (Opsional)</Label>
                <div className="border-2 border-dashed rounded-lg p-4 hover:border-primary transition-colors cursor-pointer">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageSelect}
                    className="hidden"
                    id="image-upload"
                  />
                  <label htmlFor="image-upload" className="cursor-pointer flex flex-col items-center gap-2">
                    {imagePreview ? (
                      <div className="relative w-full h-48">
                        <Image
                          src={imagePreview}
                          alt="Preview"
                          fill
                          className="object-cover rounded-lg"
                        />
                      </div>
                    ) : (
                      <>
                        <Upload className="w-12 h-12 text-gray-400" />
                        <div className="text-center">
                          <p className="text-sm font-medium">Klik untuk upload gambar</p>
                          <p className="text-xs text-gray-500">PNG, JPG, WEBP hingga 5MB</p>
                        </div>
                      </>
                    )}
                  </label>
                </div>
                {imagePreview && (
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      setSelectedImage(null);
                      setImagePreview('');
                    }}
                  >
                    Hapus Gambar
                  </Button>
                )}
              </div>

              <div className="flex justify-end space-x-2">
                <Button variant="outline" onClick={() => {
                  setIsOpen(false);
                  setSelectedImage(null);
                  setImagePreview('');
                }}>
                  Batal
                </Button>
                <Button onClick={handleAdd} disabled={uploading}>
                  {uploading ? 'Mengupload...' : 'Tambah'}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-4">
        {popularRoutes.map((popularRoute, index) => {
          const route = popularRoute.flightRoute;
          return (
            <Card key={popularRoute.id} className="p-6">
              <div className="flex items-start justify-between">
                <div className="flex items-start space-x-4 flex-1">
                  <div className="bg-yellow-100 p-3 rounded-lg">
                    <Star className="w-6 h-6 text-yellow-600" />
                  </div>
                  <div className="space-y-2 flex-1">
                    <div className="flex items-center space-x-2">
                      <span className="px-2 py-1 bg-gray-100 rounded text-sm font-medium">
                        #{popularRoute.displayOrder + 1}
                      </span>
                      <h3 className="text-xl font-bold">
                        {route.departureCity.name}  {route.arrivalCity.name}
                      </h3>
                      <span className="px-2 py-1 bg-primary/20 rounded text-sm text-primary">
                        {route.airline}
                      </span>
                      {!popularRoute.isActive && (
                        <span className="px-2 py-1 bg-red-100 rounded text-sm text-red-700">
                          Tidak Aktif
                        </span>
                      )}
                    </div>
                    <div className="grid grid-cols-3 gap-4 text-sm text-gray-600">
                      <div>
                        <span className="font-medium">Harga:</span>{' '}
                        {formatRupiah(Number(route.totalPrice))}
                      </div>
                      <div>
                        <span className="font-medium">Durasi:</span> {route.duration}
                      </div>
                      <div>
                        <span className="font-medium">Tanggal:</span>{' '}
                        {new Date(route.departureDate).toLocaleDateString('id-ID')}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <Label htmlFor={`active-${popularRoute.id}`}>Aktif:</Label>
                      <Switch
                        id={`active-${popularRoute.id}`}
                        checked={popularRoute.isActive}
                        onCheckedChange={() => handleToggleActive(popularRoute.id, popularRoute.isActive)}
                      />
                    </div>
                  </div>
                </div>
                <div className="flex flex-col space-y-2">
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => handleMoveUp(popularRoute.id, popularRoute.displayOrder)}
                    disabled={index === 0}
                  >
                    <ArrowUp className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => handleMoveDown(popularRoute.id, popularRoute.displayOrder)}
                    disabled={index === popularRoutes.length - 1}
                  >
                    <ArrowDown className="w-4 h-4" />
                  </Button>
                  <Button
                    variant="outline"
                    size="icon"
                    onClick={() => handleDelete(popularRoute.id)}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </Card>
          );
        })}
        {popularRoutes.length === 0 && (
          <Card className="p-12 text-center">
            <p className="text-gray-500">Belum ada rute populer. Tambahkan rute untuk ditampilkan di halaman utama.</p>
          </Card>
        )}
      </div>
    </div>
  );
}

src/components/admin/dynamic-fees-manage.content:
'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card } from '@/components/ui/card';
import { Plus, Trash2 } from 'lucide-react';

export interface OtherFee {
  id: string;
  name: string;
  amount: number;
}

interface DynamicFeesManagerProps {
  fees: OtherFee[];
  onChange: (fees: OtherFee[]) => void;
}

export default function DynamicFeesManager({ fees, onChange }: DynamicFeesManagerProps) {
  const [newFeeName, setNewFeeName] = useState('');
  const [newFeeAmount, setNewFeeAmount] = useState('');

  const handleAddFee = () => {
    if (!newFeeName.trim() || !newFeeAmount) {
      return;
    }

    const newFee: OtherFee = {
      id: Date.now().toString(),
      name: newFeeName.trim(),
      amount: parseFloat(newFeeAmount),
    };

    onChange([...fees, newFee]);
    setNewFeeName('');
    setNewFeeAmount('');
  };

  const handleRemoveFee = (id: string) => {
    onChange(fees.filter(fee => fee.id !== id));
  };

  const handleUpdateFee = (id: string, field: 'name' | 'amount', value: string) => {
    onChange(
      fees.map(fee => {
        if (fee.id === id) {
          if (field === 'name') {
            return { ...fee, name: value };
          } else {
            return { ...fee, amount: parseFloat(value) || 0 };
          }
        }
        return fee;
      })
    );
  };

  const formatRupiah = (amount: number) => {
    return new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0,
    }).format(amount);
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <Label className="text-base font-semibold">Rincian Biaya (Dinamis)</Label>
        <span className="text-sm text-muted-foreground">
          Total: {formatRupiah(fees.reduce((sum, fee) => sum + fee.amount, 0))}
        </span>
      </div>
      <p className="text-xs text-muted-foreground -mt-2">
        Tambahkan semua biaya penerbangan seperti: Base Fare, Tax, Service Fee, Baggage, WiFi, Meals, Insurance, dll.
      </p>

      {/* Existing Fees */}
      {fees.length > 0 && (
        <div className="space-y-2">
          {fees.map((fee) => (
            <Card key={fee.id} className="p-3">
              <div className="flex items-center gap-2">
                <div className="flex-1 grid grid-cols-2 gap-2">
                  <div className="space-y-1">
                    <Label className="text-xs">Nama Biaya</Label>
                    <Input
                      type="text"
                      value={fee.name}
                      onChange={(e) => handleUpdateFee(fee.id, 'name', e.target.value)}
                      placeholder="Contoh: Base Fare, Tax, WiFi"
                      className="h-9"
                    />
                  </div>
                  <div className="space-y-1">
                    <Label className="text-xs">Jumlah (Rp)</Label>
                    <Input
                      type="number"
                      min="0"
                      value={fee.amount}
                      onChange={(e) => handleUpdateFee(fee.id, 'amount', e.target.value)}
                      placeholder="0"
                      className="h-9"
                    />
                  </div>
                </div>
                <Button
                  type="button"
                  variant="ghost"
                  size="icon"
                  onClick={() => handleRemoveFee(fee.id)}
                  className="mt-5 text-destructive hover:text-destructive hover:bg-destructive/10"
                >
                  <Trash2 className="w-4 h-4" />
                </Button>
              </div>
            </Card>
          ))}
        </div>
      )}

      {/* Add New Fee Form */}
      <Card className="p-3 bg-muted/30">
        <div className="space-y-3">
          <div className="grid grid-cols-2 gap-2">
            <div className="space-y-1">
              <Label className="text-xs">Nama Biaya Baru</Label>
              <Input
                type="text"
                value={newFeeName}
                onChange={(e) => setNewFeeName(e.target.value)}
                placeholder="Contoh: Base Fare, Service Fee"
                className="h-9"
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    handleAddFee();
                  }
                }}
              />
            </div>
            <div className="space-y-1">
              <Label className="text-xs">Jumlah (Rp)</Label>
              <Input
                type="number"
                min="0"
                value={newFeeAmount}
                onChange={(e) => setNewFeeAmount(e.target.value)}
                placeholder="0"
                className="h-9"
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    handleAddFee();
                  }
                }}
              />
            </div>
          </div>
          <Button
            type="button"
            variant="secondary"
            size="sm"
            onClick={handleAddFee}
            className="w-full"
            disabled={!newFeeName.trim() || !newFeeAmount}
          >
            <Plus className="w-4 h-4 mr-2" />
            Tambah Biaya
          </Button>
        </div>
      </Card>

      <p className="text-xs text-muted-foreground">
         <strong>Tips:</strong> Tambahkan semua komponen biaya secara terpisah untuk transparansi penuh. 
        Contoh: Base Fare, Tax, Service Fee, Airport Fee, Fuel Surcharge, Baggage, WiFi, Meals, Insurance, Priority Boarding, dll.
      </p>
    </div>
  );
}

src/app/api/auth/me/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { cookies } from 'next/headers';

export async function GET(request: NextRequest) {
  try {
    const cookieStore = await cookies();
    const userSession = cookieStore.get('user_session');

    if (!userSession) {
      return NextResponse.json({ user: null }, { status: 200 });
    }

    const user = JSON.parse(userSession.value);
    return NextResponse.json({ user }, { status: 200 });
  } catch (error) {
    console.error('Error fetching user session:', error);
    return NextResponse.json({ user: null }, { status: 200 });
  }
}

src/app/api/payments/[id]/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { cookies } from 'next/headers';

export async function PATCH(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const cookieStore = await cookies();
    const role = cookieStore.get('session_role')?.value;

    if (role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const resolvedParams = await params;
    const body = await request.json();
    const { status, paymentProof } = body;

    const updatedPayment = await prisma.payment.update({
      where: { id: resolvedParams.id },
      data: {
        ...(status && { status }),
        ...(paymentProof && { paymentProof }),
      },
      include: {
        flightRoute: {
          include: {
            departureCity: true,
            arrivalCity: true,
          },
        },
      },
    });

    return NextResponse.json(updatedPayment);
  } catch (error) {
    console.error('Error updating payment:', error);
    return NextResponse.json(
      { error: 'Failed to update payment' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const cookieStore = await cookies();
    const role = cookieStore.get('session_role')?.value;

    if (role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const resolvedParams = await params;

    await prisma.payment.delete({
      where: { id: resolvedParams.id },
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error deleting payment:', error);
    return NextResponse.json(
      { error: 'Failed to delete payment' },
      { status: 500 }
    );
  }
}

src/app/admin/dashboard/payments/page.ts.content:
'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from '@/components/ui/dialog';
import { toast } from 'sonner';
import { formatRupiah } from '@/lib/format';
import { Upload, Check, X, Clock, Trash2, Eye } from 'lucide-react';

interface Payment {
  id: string;
  userEmail: string;
  userName: string;
  amount: string;
  status: string;
  paymentProof: string | null;
  createdAt: string;
  updatedAt: string;
  flightRoute: {
    airline: string;
    departureCity: { name: string };
    arrivalCity: { name: string };
    departureDate: string;
  };
}

export default function PaymentsPage() {
  const [payments, setPayments] = useState<Payment[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedPayment, setSelectedPayment] = useState<Payment | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [uploadingProof, setUploadingProof] = useState(false);
  const [proofImageUrl, setProofImageUrl] = useState('');

  useEffect(() => {
    fetchPayments();
  }, []);

  const fetchPayments = async () => {
    try {
      const response = await fetch('/api/payments');
      const data = await response.json();
      setPayments(data);
    } catch (error) {
      console.error('Error fetching payments:', error);
      toast.error('Gagal memuat data pembayaran');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateStatus = async (paymentId: string, newStatus: string) => {
    try {
      const response = await fetch(`/api/payments/${paymentId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        toast.success('Status pembayaran berhasil diupdate');
        fetchPayments();
        setIsModalOpen(false);
      } else {
        toast.error('Gagal mengupdate status');
      }
    } catch (error) {
      console.error('Error updating status:', error);
      toast.error('Terjadi kesalahan');
    }
  };

  const handleUploadProof = async (paymentId: string, file: File) => {
    try {
      setUploadingProof(true);
      const formData = new FormData();
      formData.append('file', file);

      const uploadResponse = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error('Upload failed');
      }

      const uploadData = await uploadResponse.json();
      const imageUrl = uploadData.url;

      const response = await fetch(`/api/payments/${paymentId}`, {
        method: 'PATCH',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ paymentProof: imageUrl }),
      });

      if (response.ok) {
        toast.success('Bukti pembayaran berhasil diupload');
        fetchPayments();
        if (selectedPayment?.id === paymentId) {
          setSelectedPayment({ ...selectedPayment, paymentProof: imageUrl });
        }
      } else {
        toast.error('Gagal menyimpan bukti pembayaran');
      }
    } catch (error) {
      console.error('Error uploading proof:', error);
      toast.error('Gagal upload bukti pembayaran');
    } finally {
      setUploadingProof(false);
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file && selectedPayment) {
      handleUploadProof(selectedPayment.id, file);
    }
  };

  const handleDeletePayment = async (paymentId: string) => {
    if (!confirm('Yakin ingin menghapus pembayaran ini?')) return;

    try {
      const response = await fetch(`/api/payments/${paymentId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        toast.success('Pembayaran berhasil dihapus');
        fetchPayments();
        setIsModalOpen(false);
      } else {
        toast.error('Gagal menghapus pembayaran');
      }
    } catch (error) {
      console.error('Error deleting payment:', error);
      toast.error('Terjadi kesalahan');
    }
  };

  const openPaymentModal = (payment: Payment) => {
    setSelectedPayment(payment);
    setIsModalOpen(true);
  };

  const getStatusBadge = (status: string) => {
    const statusConfig: Record<string, { label: string; className: string; icon: any }> = {
      pending: { 
        label: 'Pending', 
        className: 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
        icon: Clock 
      },
      paid: { 
        label: 'Lunas', 
        className: 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
        icon: Check 
      },
      cancelled: { 
        label: 'Dibatalkan', 
        className: 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300',
        icon: X 
      },
    };

    const config = statusConfig[status] || statusConfig.pending;
    const Icon = config.icon;

    return (
      <span className={`inline-flex items-center gap-1 px-3 py-1 rounded-full text-sm font-medium ${config.className}`}>
        <Icon className="w-4 h-4" />
        {config.label}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="p-6">
        <div className="text-center py-12">Loading...</div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Manajemen Pembayaran</h1>
          <p className="text-muted-foreground mt-1">
            Kelola status pembayaran dan bukti transfer
          </p>
        </div>
      </div>

      <div className="grid gap-4">
        {payments.map((payment) => (
          <Card key={payment.id} className="hover:shadow-md transition-shadow">
            <CardContent className="pt-6">
              <div className="flex items-start justify-between">
                <div className="flex-1 space-y-2">
                  <div className="flex items-center gap-3">
                    <h3 className="font-semibold text-lg">{payment.userName}</h3>
                    {getStatusBadge(payment.status)}
                  </div>
                  <p className="text-sm text-muted-foreground">{payment.userEmail}</p>
                  <p className="text-sm">
                    <span className="font-medium">Rute:</span>{' '}
                    {payment.flightRoute.departureCity.name}  {payment.flightRoute.arrivalCity.name} {' '}
                    {payment.flightRoute.airline}
                  </p>
                  <p className="text-sm">
                    <span className="font-medium">Keberangkatan:</span>{' '}
                    {new Date(payment.flightRoute.departureDate).toLocaleDateString('id-ID', {
                      weekday: 'long',
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric',
                    })}
                  </p>
                  <p className="text-2xl font-bold text-primary mt-2">
                    {formatRupiah(Number(payment.amount))}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    Dibuat: {new Date(payment.createdAt).toLocaleString('id-ID')}
                  </p>
                </div>
                <div className="flex flex-col gap-2">
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => openPaymentModal(payment)}
                  >
                    <Eye className="w-4 h-4 mr-2" />
                    Detail
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}

        {payments.length === 0 && (
          <Card>
            <CardContent className="py-12 text-center text-muted-foreground">
              Belum ada data pembayaran
            </CardContent>
          </Card>
        )}
      </div>

      {selectedPayment && (
        <Dialog open={isModalOpen} onOpenChange={setIsModalOpen}>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Detail Pembayaran</DialogTitle>
            </DialogHeader>

            <div className="space-y-4 py-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label className="text-sm text-muted-foreground">Nama</Label>
                  <p className="font-medium">{selectedPayment.userName}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Email</Label>
                  <p className="font-medium">{selectedPayment.userEmail}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Rute</Label>
                  <p className="font-medium">
                    {selectedPayment.flightRoute.departureCity.name} {' '}
                    {selectedPayment.flightRoute.arrivalCity.name}
                  </p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Maskapai</Label>
                  <p className="font-medium">{selectedPayment.flightRoute.airline}</p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Total</Label>
                  <p className="text-2xl font-bold text-primary">
                    {formatRupiah(Number(selectedPayment.amount))}
                  </p>
                </div>
                <div>
                  <Label className="text-sm text-muted-foreground">Status</Label>
                  <div className="mt-1">{getStatusBadge(selectedPayment.status)}</div>
                </div>
              </div>

              <div className="space-y-2">
                <Label>Update Status</Label>
                <Select
                  value={selectedPayment.status}
                  onValueChange={(value) =>
                    handleUpdateStatus(selectedPayment.id, value)
                  }
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="paid">Lunas</SelectItem>
                    <SelectItem value="cancelled">Dibatalkan</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Bukti Pembayaran</Label>
                {selectedPayment.paymentProof ? (
                  <div className="space-y-2">
                    <img
                      src={selectedPayment.paymentProof}
                      alt="Bukti Pembayaran"
                      className="w-full h-64 object-contain border rounded-lg"
                    />
                    <Button
                      variant="outline"
                      className="w-full"
                      onClick={() => {
                        const input = document.createElement('input');
                        input.type = 'file';
                        input.accept = 'image/*';
                        input.onchange = (e) => handleFileSelect(e as any);
                        input.click();
                      }}
                      disabled={uploadingProof}
                    >
                      <Upload className="w-4 h-4 mr-2" />
                      {uploadingProof ? 'Uploading...' : 'Ganti Bukti Pembayaran'}
                    </Button>
                  </div>
                ) : (
                  <Button
                    variant="outline"
                    className="w-full"
                    onClick={() => {
                      const input = document.createElement('input');
                      input.type = 'file';
                      input.accept = 'image/*';
                      input.onchange = (e) => handleFileSelect(e as any);
                      input.click();
                    }}
                    disabled={uploadingProof}
                  >
                    <Upload className="w-4 h-4 mr-2" />
                    {uploadingProof ? 'Uploading...' : 'Upload Bukti Pembayaran'}
                  </Button>
                )}
              </div>
            </div>

            <DialogFooter>
              <Button
                variant="destructive"
                onClick={() => handleDeletePayment(selectedPayment.id)}
              >
                <Trash2 className="w-4 h-4 mr-2" />
                Hapus
              </Button>
              <Button variant="outline" onClick={() => setIsModalOpen(false)}>
                Tutup
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

